classdef app2 < matlab.apps.AppBase

    % Properties that correspond to app components
    properties (Access = public)
        UIFigure                        matlab.ui.Figure
        INPUTEditField                  matlab.ui.control.EditField
        INPUTEditFieldLabel             matlab.ui.control.Label
        Image                           matlab.ui.control.Image
        EE19B045AbhimenyuLabel          matlab.ui.control.Label
        EE19B047MSuchitraLabel          matlab.ui.control.Label
        EE3107CommunicationSystemLabel  matlab.ui.control.Label
        ManchesterButton                matlab.ui.control.Button
        PolarRZButton                   matlab.ui.control.Button
        AMIButton                       matlab.ui.control.Button
        UniRZButton                     matlab.ui.control.Button
        PolarNRZButton                  matlab.ui.control.Button
        UniNRZButton                    matlab.ui.control.Button
        UIAxes                          matlab.ui.control.UIAxes
    end

    % Callbacks that handle component events
    methods (Access = private)

        % Button pushed function: UniNRZButton
        function UniNRZButtonPushed(app, event)

            A = 1;  %---Amplitude
            b=app.INPUTEditField.Value;%your input is a=[1,2,3,4];
            ac=str2double(strsplit(b,','));
            N = length(ac);   %---Bit Length of input bits
            x=0:1:100; %---interval of bit time period
            y = ones(1,N);
            for k = 1:N
                y = [y A*ac(k)*ones(1,length(x))];
            end
            plot(app.UIAxes,y);

        end

        % Button pushed function: UniRZButton
        function UniRZButtonPushed(app, event)
            A = 1;  %---Amplitude
             b=app.INPUTEditField.Value;%your input is a=[1,2,3,4];
            ac=str2double(strsplit(b,','));
            N = length(ac);   %---Bit Length of input bits
            x=0:1:100; %---interval of bit time period
            z = ones(1,N);
            for k = 1:N
              z = [z A*ac(k)*ones(1,round(length(x)/2)) 0*ac(k)*ones(1,round(length(x)/2))];
            end

            plot(app.UIAxes,z);
        end

        % Button pushed function: PolarNRZButton
        function PolarNRZButtonPushed(app, event)

            A = 1;  %---Amplitude
            b=app.INPUTEditField.Value;%your input is a=[1,2,3,4];
            ac=str2double(strsplit(b,','));%---Bit stream
            N = length(ac);   %---Bit Length of input bits
            x=0:1:100; %---interval of bit time period
            e = ones(1,N);
            for k = 1:N
                e = [e ((-1)^(ac(k) + 2))*A*ones(1,round(length(x)))];
            end



            plot(app.UIAxes,e);
        end

        % Button pushed function: PolarRZButton
        function PolarRZButtonPushed(app, event)

            A = 1;  %---Amplitude
            b=app.INPUTEditField.Value;%your input is a=[1,2,3,4];
            ac=str2double(strsplit(b,','));%---Bit stream
            N = length(ac);   %---Bit Length of input bits
            x=0:1:100; %---interval of bit time period
            d = ones(1,N);
             for k = 1:N
                c = ones(1,round(length(x)/2));
                b = zeros(1,round(length(x)/2));
                p = [c b];
                d = [d ((-1)^(ac(k)+2))*(A)*p];
            end


            plot(app.UIAxes,d);
        end

        % Button pushed function: ManchesterButton
        function ManchesterButtonPushed(app, event)

            A = 1;  %---Amplitude
            b=app.INPUTEditField.Value;%your input is a=[1,2,3,4];
            ac=str2double(strsplit(b,','));%---Bit stream
            N = length(ac);   %---Bit Length of input bits
            x=0:1:100; %---interval of bit time period
            f = ones(1,N);
             for k = 1:N
                c = ones(1,round(length(x)/2));
                b = -1*ones(1,round(length(x)/2));
                p = [c b];
                f = [f ((-1)^(ac(k)+1))*A*p];
            end

            plot(app.UIAxes,f);
        end

        % Button pushed function: AMIButton
        function AMIButtonPushed(app, event)
            A = 1;  %---Amplitude
            b=app.INPUTEditField.Value;%your input is a=[1,2,3,4];
            ac=str2double(strsplit(b,','));
            N = length(ac);   %---Bit Length of input bits
            x=0:1:100; %---interval of bit time period
            g = ones(1,N);
            i=0;
            for k = 1:N
                g = [g (-1)^(i)*A*ac(k)*ones(1,length(x))];
                if ac(k)==1
                    i=i+1;
                end
            end
            plot(app.UIAxes,g);
        end
    end

    % Component initialization
    methods (Access = private)

        % Create UIFigure and components
