            xlabel(app.UIAxes, 'X')
            ylabel(app.UIAxes, 'Y')
            zlabel(app.UIAxes, 'Z')
            app.UIAxes.FontName = 'AvantGarde';
            app.UIAxes.Layer = 'top';
            app.UIAxes.XGrid = 'on';
            app.UIAxes.YGrid = 'on';
            app.UIAxes.FontSize = 14;
            app.UIAxes.GridColor = [0.15 0.15 0.15];
            app.UIAxes.Box = 'on';
            app.UIAxes.Position = [23 113 595 256];

            % Create UniNRZButton
            app.UniNRZButton = uibutton(app.UIFigure, 'push');
            app.UniNRZButton.ButtonPushedFcn = createCallbackFcn(app, @UniNRZButtonPushed, true);
            app.UniNRZButton.BackgroundColor = [1 1 0];
            app.UniNRZButton.FontColor = [0 0 1];
            app.UniNRZButton.Position = [68 68 100 22];
            app.UniNRZButton.Text = 'UniNRZ';

