            app.UniRZButton.Text = 'UniRZ';

            % Create AMIButton
            app.AMIButton = uibutton(app.UIFigure, 'push');
            app.AMIButton.ButtonPushedFcn = createCallbackFcn(app, @AMIButtonPushed, true);
            app.AMIButton.BackgroundColor = [1 1 0];
            app.AMIButton.Position = [496 68 100 22];
            app.AMIButton.Text = 'AMI';

            % Create PolarRZButton
            app.PolarRZButton = uibutton(app.UIFigure, 'push');
            app.PolarRZButton.ButtonPushedFcn = createCallbackFcn(app, @PolarRZButtonPushed, true);
            app.PolarRZButton.BackgroundColor = [1 1 0];
            app.PolarRZButton.Position = [281 26 100 22];
            app.PolarRZButton.Text = 'PolarRZ';

            % Create ManchesterButton
            app.ManchesterButton = uibutton(app.UIFigure, 'push');
            app.ManchesterButton.ButtonPushedFcn = createCallbackFcn(app, @ManchesterButtonPushed, true);
            app.ManchesterButton.BackgroundColor = [1 1 0];
            app.ManchesterButton.Position = [496 26 100 22];
            app.ManchesterButton.Text = 'Manchester';

            % Create EE3107CommunicationSystemLabel
            app.EE3107CommunicationSystemLabel = uilabel(app.UIFigure);
            app.EE3107CommunicationSystemLabel.HorizontalAlignment = 'center';
            app.EE3107CommunicationSystemLabel.FontName = 'Arial Black';
            app.EE3107CommunicationSystemLabel.FontSize = 20;
            app.EE3107CommunicationSystemLabel.FontColor = [0 0.4471 0.7412];
            app.EE3107CommunicationSystemLabel.Position = [37 434 361 34];
            app.EE3107CommunicationSystemLabel.Text = 'EE3107-Communication System';

            % Create EE19B047MSuchitraLabel
            app.EE19B047MSuchitraLabel = uilabel(app.UIFigure);
            app.EE19B047MSuchitraLabel.FontName = 'Bookman';
