            app.EE19B047MSuchitraLabel.FontColor = [1 0 0];
            app.EE19B047MSuchitraLabel.Position = [196 403 139 22];
            app.EE19B047MSuchitraLabel.Text = 'EE19B047 M. Suchitra )';

            % Create EE19B045AbhimenyuLabel
            app.EE19B045AbhimenyuLabel = uilabel(app.UIFigure);
            app.EE19B045AbhimenyuLabel.FontName = 'Bookman';
            app.EE19B045AbhimenyuLabel.FontWeight = 'bold';
